-- START SNIPPET: PRESIDENT-VIEW-DDL
CREATE VIEW PRESIDENTS_VIEW AS
SELECT a.id, a.lastname, a.firstname, a.inaugurated_year, a. years, b.name AS "STATE", c.name AS "PARTY"
FROM PRESIDENT a
JOIN STATE b
ON a.state_id = b.id
LEFT OUTER JOIN PARTY c
ON a.party_id = c.id
ORDER BY 1
;
